import React, { useState } from "react";
import "./App.css";

function App() {
  const [text, setText] = useState();
  const changeUpper = () => {
    setText(text.toUpperCase());
  };
  return (
    <div className="App">
      <textarea rows="10" cols="100" />
      <br />
      <button>Upper Case</button>
      <button>Lower Case</button>
      <button>Clear Text</button>
    </div>
  );
}

export default App;
